__ht__function(data, gv, cache) {var option = {
    tooltip : {
        trigger: 'axis',
        axisPointer: {
            type: 'line',
            label: {
                backgroundColor: '#6a7985'
            }
        }
    },
    grid: {
        left: '3%',
        right: '5%',
        bottom: 0,
        top: '5%',
        containLabel: true
    },
    series: [
        {
            name:'访问来源',
            type:'pie',
            radius: [],
            avoidLabelOverlap: false,
            label: {
                normal: {
                    show: false,
                    position: 'center'
                },
                emphasis: {
                    show: true,
                    textStyle: {
                        fontSize: '30',
                        fontWeight: 'bold'
                    }
                }
            },
            labelLine: {
                normal: {
                    show: false
                }
            },
            data:[
            ]
        }
    ]
};

if (!cache.htmlView) {
    cache.htmlView = document.createElement('div');
    cache.htmlView.style.position = 'absolute';
    cache.htmlChart = echarts.init(cache.htmlView);

    // layoutHTML will be called when the data needs to be drawing
    cache.htmlView.layoutHTML = function() {
        gv.layoutHTML(data, cache.htmlView, false);
        cache.htmlChart.resize();
    };
}

// update data

option.color = data.a('lineColor');
option.series[0].radius = data.a('radius');
option.series[0].data = data.a('itemData');

cache.htmlChart.setOption(option);

// html for showing
return cache.htmlView;}
