renderHTML:
__ht__function(data, gv, cache) {var option = {
    title: {
        text: '标题'
    },
    tooltip: {
        trigger: 'axis'
    },
    grid: {
        left: '3%',
        right: '20%',
        bottom: 0,
        top: '5%',
        containLabel: true
    },
    radar: [
        {
            indicator: [

            ],
            center: ['50%','50%'],
            radius: 80
        }
    ],
    series: [
        {
            type: 'radar',
             tooltip: {
                trigger: 'item'
            },
            itemStyle: {normal: {areaStyle: {type: 'default'}}},
            data: [
                {

                }
            ]
        }
    ]
};

if (!cache.htmlView) {
    cache.htmlView = document.createElement('div');
    cache.htmlView.style.position = 'absolute';
    cache.htmlChart = echarts.init(cache.htmlView);

    // layoutHTML will be called when the data needs to be drawing
    cache.htmlView.layoutHTML = function() {
        gv.layoutHTML(data, cache.htmlView, false);
        cache.htmlChart.resize();
    };
}

// update data
option.title.text = [data.a('title')];
//option.legend.data = [data.a('legend')];
option.radar[0].indicator= data.a('indicator');
option.series[0].data[0].value = data.a('dataValue');
option.series[0].data[0].name = data.a('legend');
option.color = [data.a('itemColor')];


cache.htmlChart.setOption(option);

// html for showing
return cache.htmlView;}
